<?php
namespace Skopenow\UrlInfo;

use Skopenow\UrlInfo\EntryPoint;

class EntryPointTest extends \TestCase
{
    /** @test */
    public function normalize_url_should_return_normalized_url_using_entryPoint()
    {
        $entryPoint = new EntryPoint;
        $url = "https://www.facebook.com/people/Nicholas-Woodhams/12101952";
        $actual = $entryPoint->normalizeURL($url);
        $expected = 'https://www.facebook.com/profile.php?__sid=automation_sessions_facebook&id=12101952';
        $this->assertEquals($expected, $actual);
    }

    /** @test */
    public function should_return_twitter_as_source_using_entryPoint()
    {
        $url = "https://twitter.com/Ahmed_Abrass";
        $entryPoint = new EntryPoint;
        $actual = $entryPoint->determineSource($url)[0];
        $this->assertEquals('twitter', $actual);
    }

    /** @test */
    public function should_return_twitter_as_main_source_using_entryPoint()
    {
        $url = "https://twitter.com/Ahmed_Abrass";
        $entryPoint = new EntryPoint;
        $actual = $entryPoint->determineSource($url)[1];
        $this->assertEquals('twitter', $actual);
    }

    /** @test */
    public function should_return_profile_site_tag_using_entryPoint()
    {
        $url = "https://twitter.com/Ahmed_Abrass";
        $entryPoint = new EntryPoint;
        $actual = $entryPoint->getSiteTag($url);
        $this->assertEquals('Profile', $actual);
    }

    /** @test */
    public function prepare_content_should_prune_url_proberly_using_entryPoint()
    {
        $url = "https://www.facebook.com/events/210473292817026/?";
        $entryPoint = new EntryPoint;
        $actual = $entryPoint->prepareContent($url);
        $expected = 'http://facebook.com/events/210473292817026';
        $this->assertEquals($expected, $actual);
    }

    /** @test */
    public function should_return_true_with_real_producthunt_profile_using_entryPoint()
    {
        $htmlContent = ['header' => ['http_code' => 200, 'url' => '"https://www.producthunt.com/mohammed']];
        $url = "https://www.producthunt.com/about/me";
        $entryPoint = new EntryPoint;
        $actual = $entryPoint->profileExists($url, $htmlContent);
        $this->assertEquals(true, $actual);
    }

    /** @test */
    public function should_return_true_with_real_twitter_profile_using_entryPoint()
    {
        $url = "https://twitter.com/mohammed";
        $entryPoint = new EntryPoint;
        $actual = $entryPoint->isProfile($url);
        $this->assertEquals(true, $actual);
    }

    /** @test */
    public function should_return_false_with_twitter_status_url_using_entryPoint()
    {
        $url = "https://twitter.com/status/123456799";
        $entryPoint = new EntryPoint;
        $actual = $entryPoint->isProfile($url);
        $this->assertEquals(false, $actual);
    }

    /** @test */
    public function should_return_profile_username_using_entryPoint()
    {
        $url = "https://twitter.com/mohammed";
        $entryPoint = new EntryPoint;
        $actual = $entryPoint->getUsername($url);
        $expected = "mohammed";
        $this->assertEquals($expected, $actual);
    }

